import { Component, EventEmitter, Output } from '@angular/core';
import { Character } from '../../interfaces/character,.interface';

@Component({
  selector: 'dbz-add-character',
  templateUrl: './add-character.component.html',
  styleUrls: ['./add-character.component.css']
})
export class AddCharacterComponent {

  @Output()
  public onNewCharacter: EventEmitter<Character> = new EventEmitter();

  public character: Character= {
    name: '',
    power: 0
  };


  emitCharacter(): void{

    console.log(this.character);

    if(this.character.name.length === 0) return;

    this.onNewCharacter.emit(this.character);

    this.character.name = '';
    this.character.power = 0;
  }
}
<h4>Agregar Personaje</h4>

<form class="row" action="">

  <input type="text" [(ngModel)]="character.name" name="name" class="form-control mb-2" placeholder="Nombre">

  <input type="text" [(ngModel)]="character.power" name="power" class="form-control mb-2" placeholder="Poder">

  <button class="btn btn-primary" type="submit" (click)="emitCharacter()">
    Agregar
  </button>

  <pre>{{character | json}}</pre>
</form>
